class Solution {
    int binPow(int num, int p, int m){
        long long int ans=1;
        long long int n=num;
        while (p){
            if (p&1) ans=((1LL*ans%m)*(n%m))%m;
            n = ((1LL*n%m)*(n%m))%m;
            p/=2;
        }
        return ans;
    }
    bool fermatPrime(int p){
        if (p<=1) return false;
        if (p==2 || p==3 || p==5 or p==7 or p==11) return true;
        for (int i=1;i<=5;i++){
            int a=2+rand()%(p-3);
            if (binPow(a,p-1,p)!=1) return false;
        }
        return true;
    }
    bool pal(int n){
        int num=n;
        int ret=0;
        while (num){
            int rem=num%10;
            ret=ret*10+rem;
            num/=10;
        }
        return ret==n;
    }
