class Solution {
    public int primePalindrome(int N) {
        if(reverse(N) == N && isPrime(N))
            return N;
        int next = N;
        while (true) {
            String nextString = findNext(next);
            next = Integer.parseInt(nextString);
            if(next == 11)
                return next;
            if(nextString.length()%2 == 0){
                int length = nextString.length();
                nextString = "1";
                while(length-- > 0)
                    nextString += "0";
                next = Integer.parseInt(nextString);
            }
            else if(isPrime(next))
                return next;
        }
    }

    public boolean isPrime(int N) {
        if (N < 2) return false;
        int R = (int) Math.sqrt(N);
        for (int d = 2; d <= R; ++d)
            if (N % d == 0) return false;
        return true;
    }

    public int reverse(int N) {
        int ans = 0;
        while (N > 0) {
            ans = 10 * ans + (N % 10);
            N /= 10;
        }
        return ans;
    }
    private String findNext(int num){
        String s = Integer.toString(num);
        char [] n = s.toCharArray();
        int start = 0;
        int end = n.length - 1;
        boolean reverse = true;
        while(start <= end){
            if(n[start] < n[end]){
                n[end] = n[start];
                reverse = true;
            }
            else if(n[start] > n[end]){
                n[end] = n[start];
                reverse = false;
            }
            start++;
            end --;
        }
        if(!reverse)
            return new String(n);
        start--;
        end++;
        while(start >=0 && n[start] == '9'){
            n[start] = '0';
            n[end] = '0';
            start--;
            end++;
        }
        if(start < 0){
            n[end-1] = '1';
            return "1"+new String(n);
        }
        else{
            char temp  = (char)(n[start] +1);
            n[start] = temp;
            n[end] = temp;
            return new String(n);
        }
    }
}

