func primePalindrome(N int) int {
	numberInStr := strconv.Itoa(N)
	numberInBytes := []byte(numberInStr)
	for i := 0; i < len(numberInBytes)/2; i++ {
		numberInBytes[len(numberInBytes)-1-i] = numberInBytes[i]
	}
	if string(numberInBytes) < numberInStr {
		numberInBytes = next(numberInBytes)
	}
	for !isPrime(string(numberInBytes)) {
		numberInBytes = next(numberInBytes)
	}
	v, _ := strconv.Atoi(string(numberInBytes))
	return v
}

func next(number []byte) []byte {
	m := len(number) / 2
	if len(number)%2 == 0 {
		m--
	}
	for m >= 0 {
		if number[m] != '9' {
			break
		}
		m--
	}
	if m < 0 {
		number[0] = '1'
		for i := 1; i < len(number); i++ {
			number[i] = '0'
		}
		number = append(number, '1')
		return number
	}
	number[m]++
	if len(number)-1-m != m {
		number[len(number)-1-m]++
	}
	for i := m + 1; i < len(number)/2; i++ {
		number[i] = '0'
		number[len(number)-1-i] = '0'
	}
	if len(number)%2 != 0 {
		if m != len(number)/2 {
			number[len(number)/2] = '0'
		}
	}
	return number
}

func isPrime(v string) bool {
	if v == "1" {
		return false
	}
	if v == "2" {
		return true
	}
	vi, _ := strconv.Atoi(v)
	if vi%2 == 0 {
		return false
	}
	m := int(math.Sqrt(float64(vi)))
	for i := 3; i <= m; i += 2 {
		if vi%i == 0 {
			return false
		}
	}
	return true
}
