    public int primePalindrome(int N) {
        while (true) {
            if (N == reverse(N) && isPrime(N))
                return N;
            N++;
            if (10000000 < N && N < 100000000) N = 100000000;
        }
    }

    public boolean isPrime(int N) {
        if (N < 2) return false;
        for (int i = 2; i * i <= N; i++) {
            if (N % i == 0) return false;
        }
        return true;
    }

    public int reverse(int N) {
        int num = 0;
        while (N > 0) {
            num = 10 * num + (N % 10);
            N /= 10;
        }
        return num;
    }
