impl Solution {
    pub fn prime_palindrome(mut n: i32) -> i32 {
        fn sqrt(x: i32) -> i32 {
            if x < 2 {
                return x;
            };
            let mut left = 1;
            let mut right = x;
            let mut middle = 1;
            loop {
                middle = (left + right) / 2;
                if middle > x / middle {
                    right = middle - 1;
                } else {
                    left = middle + 1;
                    if left > x / left {
                        return middle;
                    }
                }
            }
        }

        fn is_prime(n: i32) -> bool {
            if n < 2 {
                return false;
            } else if n == 2 {
                return true;
            }
            if n % 2 == 0 {
                return false;
            }
            for i in (3..sqrt(n) + 1).step_by(2) {
                if n % i == 0 {
                    return false;
                }
            }
            true
        }

        match n {
            1 => 2,
            2 => 2,
            3 => 3,
            4 => 5,
            5 => 5,
            6 => 7,
            7 => 7,
            8..=11 => 11,
            _ => {
                for x in 1..100000 {
                    let mut s = x.to_string();
                    let len = s.len();
                    let r = s.chars().rev().skip(1).collect::<String>();
                    s.push_str(&r);
                    let y = s.parse::<i32>().unwrap();
                    if y >= n && is_prime(y) {
                        return y;
                    }
                }
                std::i32::MIN // unreachable!()
            }
        }
    }
}

