                nDigits[nDigits.Count - 1 - i] = nDigits[i];
            }

            do
            {
                var res = Helper(nDigits, num, 0);

                if (res > 0)
                {
                    return res;
                }

                for (int i = 0; i < nDigits.Count; i++)
                {
                    nDigits[i] = 0;
                }
                nDigits[0] = 1;
                nDigits.Add(1);

            } while (true);
        }
    }
