class Solution {
public:
    int primePalindrome(int N);
    bool isPrime(int n);
    bool isPalindrome(int n);
    int nextPalindrome(int n);
    int firstPalindrome(int n);
};

bool Solution::isPrime(int n)
{
	for (int i = 2; i*i <= n; i++) {
		if (n % i == 0) 
			return false;
	}
	return true;
}

bool Solution::isPalindrome(int n)
{
	stringstream ss;
	ss << n;
	string str = ss.str();
	int i = 0, j = str.size()-1;
	while (i < j) {
		if (str[i++] != str[j--])
			return false;
	}
	return true;
}

int Solution::nextPalindrome(int n)
{
	stringstream ss;
	ss << n;
	string str = ss.str();

	int i, j;
	i = j = str.size()/2;
	if (str.size() % 2 == 0) {
		i--;
	} 

	while (i >= 0) {
		if (str[i] == '9') {
			str[i] = str[j] = '0';
		} else {
			str[i]++;
			str[j] = str[i];
			break;
		}
		i--;
		j++;
	}

	if (i < 0) {
		str += '1';
		str[0] = '1';
	}
	return atoi(str.c_str());
}

int Solution::firstPalindrome(int n)
{
	stringstream ss;
	ss << n;
	string str = ss.str();

	int i, j;
	i = j = str.size()/2;
	if (str.size() % 2 == 0) {
		i--;
	} 

	while (i > 0) {
		if (str[i] == '9') {
			str[j++] = str[i--] = '0';
			continue;
		} else {
			str[i]++;
			break;
		}	
	}

	while (i >= 0) {
		str[j] = str[i];
		i--;
		j++;
	}

	return atoi(str.c_str());
}

int Solution::primePalindrome(int N)
{
	int n = N;
	if (n == 1)  n = 2;

	if (!isPalindrome(n)) 
		n = firstPalindrome(n);

	while (true) {
		//cout << n << endl;
		if (n >= N)
			if (isPrime(n))
				break;
		n = nextPalindrome(n);
	}

	return n;
}
```

