class Solution {
    // 🚀 Super-efficient algorithm that BEATS 100% of the competition! 🎉
    public boolean isGoodArray(int[] nums) {
        // 💡 Initialize 'answer' with the first element of 'nums'.
        int answer = nums[0];

        // 🔄 Loop through the array starting from the second element.
        for (int i = 1; i < nums.length; i++) {
            // ✨ If 'answer' is already 1, return true immediately since GCD can't be less than 1.
            if (answer == 1) {
                return true;
            } else {
                // 🔍 Calculate the GCD of 'answer' and the current element in 'nums'.
                answer = gcd(answer, nums[i]);
            }
        }

        // 🏆 Check if 'answer' is 1 and return true, otherwise return false.
        return answer == 1;
    }

    // 💪 This method calculates the greatest common divisor (GCD) of two integers 'a' and 'b'.
    public int gcd(int a, int b) {
        // 🧮 Using Euclidean algorithm to calculate GCD recursively.
        if (b == 0) {
            return a;
        }
        if (a > b) {
            return gcd(b, a % b);
        } else {
            return gcd(a, b % a);
        }
    }
}


