class Solution {
    public boolean isGoodArray(int[] nums) {
       int n = nums.length;
       int ans = nums[0] ;

       for( int i = 1; i < n; i++) {
           ans = gcd( ans, nums[i] );
       }

       return ans == 1;
    }

    //  greatest common divisor (gcd) function
    public int gcd( int a, int b ) {
        // check
        if( a == 0 ) {
            return b;
        }

        return gcd( b%a, a );
    }
}
