            gcd_till_now=gcd(gcd_till_now,nums[i]);
            if(gcd_till_now==1) return true;
        }

        return false;
    }

    static int gcd(int a, int b){

        //Euclid Algo to find GCD of two numbers
        if(b==0) return a;

        return gcd(b,a%b);
    }
}
