            dp[i+1] = 2+dp[prev_box+1]+trips[i]-trips[prev_box+1];
            while (asc_dq.size()) {
                int prev_box = asc_dq.back();
                int last = dp[prev_box+1]-trips[prev_box+1];
                if (dp[i+1]-trips[i+1] <= last) {
                    asc_dq.pop_back(); continue;
                }
                break;
            }
            asc_dq.push_back(i);
        }
        return dp.back();
    }
};
