class Solution {
    public int boxDelivering(int[][] box, int __, int max, int limit) {
        int n = box.length;
        int[] dp = new int[n+1]; // Minimum trips for first n boxes.
        int weight = 0; // cumulative weight
        int cost = 2; // cumulative cost

        int l = 0;  // left ptr
        for (int r = 0; r < n; r++){ // right ptr
            // New box
            weight += box[r][1];
            if (r!=0 && box[r][0] != box[r-1][0]) cost++;
            /* drop boxes iff:
                - There are too many boxes (r-l >= max)
                - The boxes are too heavy (weight > limit)
                - It is redundant to carry them (dp[l] == dp[l+1]).
            */
            while(r-l >= max || weight > limit || (l < r && dp[l] == dp[l+1])){
                weight -= box[l][1];
                if (box[l+1][0] != box[l][0]) cost--;
                l++;
            }
            dp[r+1] = cost + dp[l];
        }
        return dp[n];
    }
}
