        for(int r=0;r<n;r++){
            cW+=A[r][1];
            while(cW>W or r-l+1>B){
                cW-=A[l][1];
                l+=1;
            }
            // [l,r] is maximum window that ship can take if the last box it take is A[r]
			// calc olddp here
			// ...
        }
        return olddp;
