class Solution:
    def boxDelivering(self, A, __, B, W):
        n = len(A)
        def slidingWindow():
            l=0
            cW = 0
            for r in range(n):
                cW+=A[r][1]
                while cW>W or r-l+1>B:
                    cW-=A[l][1]
                    l+=1
                yield l,r
        Seg=MonoQueue()
