class Solution {
public:
    int boxDelivering(vector<vector<int>>& A, int __, int B, int W) {
        int n = A.size();
        int ans = INT_MAX;
        int l=0; 
        int cW = 0;
        // saves <value, index> so that it put min value before other value
        multiset<pair<int,int>> seg; 
        int addAll = 0;
        int olddp=0;
        for(int r=0;r<n;r++){
            cW+=A[r][1];
            while(cW>W or r-l+1>B){
                cW-=A[l][1];
                l+=1;
            }
            if(r!=0)
                addAll += (A[r][0]!=A[r-1][0]);
            seg.insert(make_pair(olddp-addAll+2, r));
            while(seg.begin()->second<l){
                seg.erase(begin(seg));
            }
            olddp = (seg.begin()->first) + addAll;
        }
        return olddp;
    }
};
