        int lastCountTrip = -1;
        int countTrip = 1; //1 means the back trip
        //transitions records how many transitions before the back trip
        int j, transitions = 0;
        for(j=i; j>=0; j--) {
            countWeight += boxes[j][1]; countBox += 1;
            if(countBox > maxBoxes || countWeight > maxWeight) {break;}
            if(boxes[j][0] != lastPort){
                transitions += 1;
                lastPort = boxes[j][0]; lastPortIdx = j;
                lastCountTrip = countTrip; countTrip += 1;
            }
        }
        //greedy case 1
        ret = min(ret, countTrip + dfs(boxes, maxBoxes, maxWeight, j));
        //greedy case 2, if we see number of transitions that is more than 1
        if(transitions > 1) {
            ret = min(ret, lastCountTrip + dfs(boxes, maxBoxes, maxWeight, lastPortIdx));    
        }

        dp[i] = ret;
        return ret;
    }
};
