class Solution {
public:
    int boxDelivering(vector<vector<int>>& boxes, int portsCount, int maxBoxes, int maxWeight) {
        int n=boxes.size();
        map<int,int> dp;
        dp[-1]=0;
        multiset<int> s;
        vector<int> distinct={0};
        int wt=boxes[0][1];
        s.insert(dp[-1]-0);
        dp[0]=2;
        int l=0;
        for(int r=1;r<n;r++){
            distinct.push_back(
                (boxes[r][0]!=boxes[r-1][0])+distinct.back());
            wt+=boxes[r][1];
            while(wt>maxWeight or r-l+1>maxBoxes){
                s.erase(s.find(dp[l-1]-distinct[l]));
                wt-=boxes[l][1];
                l++;
                assert(l<=r);
            }
            s.insert(dp[r-1]-distinct[r]);
            dp[r]=*s.begin()+distinct.back()+2;
        }
        return dp[n-1];
    }
};
