class Solution {
public:
    int boxDelivering(vector<vector<int>>& boxes, int portsCount, int maxBoxes, int maxWeight) {
        vector<pair<long long,long long>> a;
        vector<int> b_count;
        long long boxes_count = 1, weight = boxes[boxes.size()-1][1], port = boxes[boxes.size()-1][0]; 
        for (int i = boxes.size() - 2; i >=0;i--) {
            int p1 = boxes[i][0], w1 = boxes[i][1];
            if (p1 == port && boxes_count + 1 <= maxBoxes && w1+weight <= maxWeight) {
                weight += w1;
                boxes_count++;
            }
            else {
                a.push_back({port,weight});
                b_count.push_back(boxes_count);
                port = p1;
                boxes_count = 1;

                weight = w1;
            }
        }
        a.push_back({port, weight});
        b_count.push_back(boxes_count);
        reverse(b_count.begin(), b_count.end());
        reverse(a.begin(), a.end());
        int ans = 0;
        boxes_count = b_count[0];
        weight= a[0].second;
        port = a[0].first;
        int unique = 1;
        for (int i = 1 ; i < a.size();i++) {
            int w = a[i].second, b = b_count[i];
            if (b + boxes_count <= maxBoxes && w + weight <= maxWeight) {
                if (a[i].first != port) {
                    unique++;
                    port = a[i].first;
                }
                boxes_count += b;
                weight += w;
            }
            else {
                ans += unique +1;
                unique = 1;
                boxes_count = b;
                weight = w;
                port = a[i].first;
            }
        }
        ans+=unique+1;
        return ans;
    }
};

