class DetectSquares {
    unordered_map<int, unordered_map<int,int>> graph;
public:
    DetectSquares() {

    }

    void add(vector<int> point) {
        graph[point[0]][point[1]]++;
    }

    int count(vector<int> point) {
        int count = 0, x1 = point[0], y1 = point[1];

        for(auto &y2 : graph[x1]){
            if(y2.first == y1)
                continue;
            int diff = abs(y1 - y2.first);
            int xL = point[0] - diff;
