public class DetectSquares {
    // two-dimensional dictionary where the first dictionary key is x coordinate, 
    // the second dictionary is y coordinate and value is the count of points
    private IDictionary<int, IDictionary<int, int>> _pointsDict;

    public DetectSquares() {
        _pointsDict = new Dictionary<int, IDictionary<int, int>>();
    }

    public void Add(int[] point) {
        var x = point[0];
        var y = point[1];

        if(!_pointsDict.ContainsKey(x)) {
            _pointsDict[x] = new Dictionary<int, int>();
        }

        _pointsDict[x][y] = (_pointsDict[x].ContainsKey(y) ? _pointsDict[x][y] : 0) + 1;
    }

    public int Count(int[] point) {
        var inputX = point[0];
        var inputY = point[1];

        // if there aren't points which can form squares 
        if(!_pointsDict.ContainsKey(inputX)) return 0;

        var totalCount = 0;

        // checking all points on the same Y-vertical 
        // whether possible to detect square and count them
        foreach(var pair in _pointsDict[inputX]) {
            var currentY = pair.Key;

            var sideLength = Math.Abs(currentY - inputY);

            // skip the same point
            if(sideLength == 0) continue;

            // count squares on the left side of Y-vertical 
            totalCount += GetPointsCount(inputX, currentY) * GetPointsCount(inputX - sideLength, currentY) * GetPointsCount(inputX - sideLength, inputY);

            // count squares on the right side of Y-vertical  
            totalCount += GetPointsCount(inputX, currentY) * GetPointsCount(inputX + sideLength, currentY) * GetPointsCount(inputX + sideLength, inputY);
        }

        return totalCount;
    }

    private int GetPointsCount(int x, int y) {
        if(!_pointsDict.ContainsKey(x)) return 0;
        if(!_pointsDict[x].ContainsKey(y)) return 0;

        return _pointsDict[x][y];
    }
}
