class DetectSquares {
public:
    unordered_map<int,unordered_map<int,int>>mp;

    void add(vector<int> point) {
        mp[point[0]][point[1]]+=1;
    }

    int count(vector<int> point) {
        int ans=0;
        int x=point[0];
        int y=point[1];
        for(auto it=mp[x].begin();it!=mp[x].end();it++){
            int y2=(*it).first;
            if(y2==y)continue;
            int side=abs(y-y2); //length of side of square.
            if(mp.find(x-side)!=mp.end()){
                if(mp[x-side].find(y)!=mp[x-side].end()&&mp[x-side].find(y2)!=mp[x-side].end()){
                    ans+=(mp[x-side][y]*mp[x-side][y2]*(*it).second);
                }
