                if(mp[x+side].find(y)!=mp[x+side].end()&&mp[x+side].find(y2)!=mp[x+side].end()){
                    ans+=(mp[x+side][y]*mp[x+side][y2]*(*it).second);
                }
            }

        }
        return ans;
    }
};
