class DetectSquares:

    def __init__(self):
        self.axis_x = {}

    def add(self, point: List[int]) -> None:
        x, y = point[0], point[1]
        if x not in self.axis_x:
            self.axis_x[x] = {}
        if y not in self.axis_x[x]:
            self.axis_x[x][y] = 0
        self.axis_x[x][y] += 1

    def count(self, point: List[int]) -> int:
        x, y = point[0], point[1]
        if x not in self.axis_x:
            return 0
        res = 0
        for p_y in self.axis_x[x]:
            if p_y != y:
                distance = abs(y - p_y)
                l, r = x - distance, x + distance
                if l in self.axis_x and y in self.axis_x[l] and p_y in self.axis_x[l]:
                    res += self.axis_x[x][p_y] * self.axis_x[l][y] * self.axis_x[l][p_y]
                if r in self.axis_x and y in self.axis_x[r] and p_y in self.axis_x[r]:
                    res += self.axis_x[x][p_y] * self.axis_x[r][y] * self.axis_x[r][p_y]
