        int &y = point[1];
        if (!xToy.count(x) || !xToy[x].count(y)) {
            xToy[x][y] = 0;
            distinct.push_back(point);
        }
        xToy[x][y]++;
    }

    int queryCnt(int x,int y) {
        if (!xToy.count(x) || !xToy[x].count(y)) {
            return 0;
        }
        return xToy[x][y];
    }

    int count(vector<int> point) {
        int &x1 = point[0];
        int &y1 = point[1];
        int res = 0;
        for (auto &ele : distinct) {
            int &x2 = ele[0];
