var DetectSquares = function() {
  this.map = {}; // {'x1,y1': 1, 'x2,y2': 4, ....}
  this.points = []; // [[x1, y1], [x2, y2], [x3, y3], ....]
};

/** 
 * @param {number[]} point
 * @return {void}
 */
DetectSquares.prototype.add = function(point) {
  let str = point[0] + ',' + point[1];
  if(!this.map[str])
    this.map[str] = 0;
  this.map[str]++;
  this.points.push(point); // because we want duplicate points as well!
};

/** 
 * @param {number[]} point
 * @return {number}
 */
DetectSquares.prototype.count = function(point) {
  // find using diagonal properties
  // from the map find if p3 exists ( diagonal of given point i.e. p1)
  // conditions to check:
  // 1. They don't lie on same X axis
  // 2. Difference of X and Y are equal
  let res = 0
  let [x1, y1] = point;
  for(let [x3, y3] of this.points) {
    if(x1 === x3 || Math.abs(x1 - x3) !== Math.abs(y1 - y3))
      continue;
    let [x2, y2] = [x3, y1];
    let [x4, y4] = [x1, y3];
    let p2 = x2 + ',' + y2;
    let p4 = x4 + ',' + y4;

    res = res + ((this.map[p2] || 0) * (this.map[p4] || 0));
  }
  return res;
};
