class DetectSquares {
public:
    map<pair<int,int>,int> points;
    DetectSquares() {

    }

    void add(vector<int> point) {
        points[{point[0],point[1]}]++;
    }

    int count(vector<int> point) {

        // First Point
        pair<int,int> p1={point[0],point[1]};        
        int ans=0;
        for(auto p:points){

            // Second Point
            pair<int,int> p2=p.first;

            // If Edge Between p1 and p2 is Vertical
            if(p2.first==p1.first){

                // Calculate Edge Length
                int side=abs(p2.second-p1.second);

                // If Both p1 and p2 coincide(Zero Area).
                if(side==0) continue;

                // 2 other points
                pair<int,int> p3,p4;

                // Case 1: Take p3 and p4 on left side of edge b/w p1 and p2.
                p3={p1.first-side,p1.second};
                p4={p2.first-side,p2.second};
                ans+=points[p2]*points[p3]*points[p4];

                // Case 1: Take p3 and p4 on right side of edge b/w p1 and p2.
                p3={p1.first+side,p1.second};
                p4={p2.first+side,p2.second};
                ans+=points[p2]*points[p3]*points[p4];
            }
        }
        return ans;
    }
};

