class DetectSquares {
    int[][] matrix;
    Map<Integer, Set<Integer>> coor;
    public DetectSquares() {
        this.matrix = new int[1001][1001];
        coor = new HashMap<>();
    }

    public void add(int[] point) {
        this.matrix[point[0]][point[1]] += 1;
        coor.putIfAbsent(point[0], new HashSet<>());
        coor.get(point[0]).add(point[1]);
    }
    // Make point as one corner(left bottom or right top), 
    // Select another point as another corner (right top or left bottom)
    // Check if it is valid or not
    public int count(int[] point) {
        int res = 0;
        int x = point[0];
        int y = point[1];
        for (int i : coor.keySet()) {
            Set<Integer> set = coor.get(i);
            for (int j : set) {
                if (matrix[i][j] > 0 && 
                    matrix[x][j] > 0 && 
                    matrix[i][y] > 0 && 
                    i != x && j != y && 
                    Math.abs(x - i) == Math.abs(y - j)) {
                    res += matrix[i][j] * matrix[x][j] * matrix[i][y];
                }
            }
        }
        return res;
    }
}

