class DetectSquares {

    Map<Integer, Map<Integer, Integer>> yx;


    public DetectSquares() {
        yx = new HashMap<>();
    }

    public void add(int[] point) {
        int x = point[0];
        int y = point[1];

        if(yx.get(y) == null){
            yx.put(y, new HashMap<>());
        }
        yx.get(y).put(x, yx.get(y).getOrDefault(x, 0)+1);
    }

    public int count(int[] point) {

        int x = point[0];
        int y = point[1];

        int sum = 0;

        if(yx.get(y) != null){

            Map<Integer, Integer> map = yx.get(y);

            for(Map.Entry<Integer, Integer> entry : map.entrySet()){

                int x1 = entry.getKey();

                if(Math.abs(x1 - x) > 0){

                    int dist = Math.abs(x1 - x);

                    int yNew = y + dist;

                    if(yx.get(yNew) != null && yx.get(yNew).get(x) != null && yx.get(yNew).get(x1) != null){
                        sum = sum + yx.get(yNew).get(x)*entry.getValue()*yx.get(yNew).get(x1);
                    }

                    yNew = y - dist;

                    if(yx.get(yNew) != null && yx.get(yNew).get(x) != null && yx.get(yNew).get(x1) != null){
                        sum = sum + yx.get(yNew).get(x)*entry.getValue()*yx.get(yNew).get(x1);
                    }

                }

            }

        }

        return sum;

    }
}

