public:
    vector<vector<int>> graph{1001, vector<int>(1001, 0)};
    DetectSquares() {

    }

    void add(vector<int> point) {
        int x = point[0];
        int y = point[1];
        graph[x][y]++;
    }

    int count(vector<int> point) {
        int x = point[0];
        int y = point[1];
        int count = 0;

        /*For every point (x,y), we can find square around it of len = 1 to 1000
         _____________
        |  2   |  1   |
        |_____(x,y)___|
        |    3 |  4   |
        |______|______|
        */

        for(int len = 1; len <=1000; len++){
            // finding any square 1 of len
            if(x-len >= 0 && y+len <= 1000)
                count += graph[x-len][y+len] * graph[x-len][y] * graph[x][y+len];

            // finding any square 2 of len
            if(x-len >= 0 && y-len >= 0)
                count += graph[x-len][y-len] * graph[x-len][y] *  graph[x][y-len];

            // finding any square 3 of len
            if(x+len <= 1000 && y-len >=0)
                count += graph[x+len][y-len] * graph[x+len][y] * graph[x][y-len];

            // finding any square 4 of len
            if(x+len <=1000 && y+len <= 1000)
                count += graph[x+len][y+len] * graph[x+len][y] * graph[x][y+len];
        }
        return count;
    }
};

