package main

type DetectSquares struct {
	cnt map[[2]int]int
}

func Constructor() DetectSquares {
	return DetectSquares{
		cnt: map[[2]int]int{},
	}
}

func (this *DetectSquares) Add(point []int) {
	this.cnt[[2]int{point[0], point[1]}]++
}

func abs(a int) int {
	if a < 0 {
		return -a
	}
	return a
}

func (this *DetectSquares) Count(point []int) int {
	px, py := point[0], point[1]
	x, y := px, py

	res := 0
	for k, cnt1 := range this.cnt {
		x, y = k[0], k[1]
		if abs(x-px) != abs(y-py) || (x == px && y == py) {
			continue
		}
		cnt2, f2 := this.cnt[[2]int{x, py}]
		cnt3, f3 := this.cnt[[2]int{px, y}]
		if f2 && f3 {
			res += cnt1 * cnt2 * cnt3
		}
	}
	return res
}

