class DetectSquares {
    HashMap<Pair<Integer, Integer>, Integer> map;
    List<Pair<Integer, Integer>> pointsLst;
    public DetectSquares() {
        pointsLst = new ArrayList<>();
        map = new HashMap<>();
    }

    public void add(int[] point) {
        Pair<Integer, Integer> pair = new Pair(point[0], point[1]);
        map.put(pair, map.getOrDefault(pair, 0) + 1);
        pointsLst.add(pair);

    }

    public int count(int[] point) {

        int res = 0;
        int qx = point[0];
        int qy = point[1];

        for (int i = 0; i < pointsLst.size(); i++) {
            int x = pointsLst.get(i).getKey();
            int y = pointsLst.get(i).getValue();
            if ((x == qx && y == qy) || Math.abs(x - qx) != Math.abs(y - qy)) {
                continue;
            }
            Pair<Integer, Integer> newKey = new Pair(x, qy);
            Pair<Integer, Integer> newKey1 = new Pair(qx, y);
            if (map.containsKey(newKey) && map.containsKey(newKey1)) {
                res += map.get(newKey) * map.get(newKey1);
            }
        }

        return res;
    }
}

