class DetectSquares {
public:
    map<pair<int, int>,int> mp;

    DetectSquares() {
        return ;
    }

    void add(vector<int> point) {
        // add the pair to the map to keep count of same numbers
        mp[{point[0],point[1]}]++;
        return ;
    }

    int count(vector<int> point) {
        int count = 0;
        int x1= point[0], y1 = point[1];

        // traverse the map to find the rest 3 points of square 
        // considering x1,y1 is first point
        for(auto pt : mp){
            pair<int,int> it = pt.first;
            // find the diagonal point of x1,y1 i.e. find x3,y3
            if(it.first!=x1 && it.second!=y1 && (abs(it.first-x1)==abs(it.second-y1))){
                // now we need to find x2,y2 and x4,y4
                // there corrdinates are (x3,y1) and (x1,y3) 
                // i.e. it.first,y1 & x1,it.second
                // number of squares will be multiply of counts of x2,y2 x3,y3 & x4,y4
                count += mp[{it.first,it.second}]*mp[{it.first,y1}]*mp[{x1,it.second}];
            }
        }
        return count;
    }
};

