class DetectSquares {

    /*
     * To maintain unique values and frequencies of each point.
     */
    Map<Pair<Integer, Integer>, Integer> point_counts;
    public DetectSquares() {
        point_counts = new HashMap<>();
    }

    /*
     * Add point in the map and check if it already exists. 
     * If yes, increment the counter
     * If no, put 1
    */
    public void add(int[] point) {
       Pair<Integer, Integer> p = new Pair(point[0],point[1]);
       point_counts.put(p, point_counts.getOrDefault(p,0) + 1);
    }

    /*
     * This method goes through every entry in the map and checks if the respective entry is diagonal
     * If yes, 
     *    1) check if row of the diagonal point and column of the input point exist in the map
     *    2) check if the column of diagonal point and row of the input point exist in the map
     *    3) If both the above conditions are correct check if the diagonal points have same distance (to verify that the points are square and NOT a rectangle)
     *          if all 3 conditions satisfy, multiply all their frequencies and add that the the resulting count variable (i.e. square_count)
     * 
    */
    public int count(int[] point) {   
        int square_count = 0;
        for(Pair<Integer, Integer> diagonal_corner : point_counts.keySet()){


            int diagonal_row = diagonal_corner.getKey();
            int diagonal_col = diagonal_corner.getValue();

            boolean matching_row = (diagonal_row == point[0]);
            boolean matching_col = (diagonal_col == point[1]);
            boolean is_square = (Math.abs(diagonal_row - point[0]) == Math.abs(diagonal_col - point[1]));

            if( !matching_row && !matching_col && is_square){
                Pair<Integer, Integer> corner1 = new Pair(point[0],diagonal_col);
                Pair<Integer, Integer> corner2 = new Pair(diagonal_row, point[1]);

                int freq_diagonal = point_counts.getOrDefault(diagonal_corner,0);
                int freq1 = point_counts.getOrDefault(corner1,0);
                int freq2 = point_counts.getOrDefault(corner2,0);

                square_count += (freq_diagonal * freq1 * freq2);
            }
        }
        return  square_count;
    }
}
