            for (int j = 0; j <= 1000; j++) {
                counts[i][j] = 0;
            }
        }
    }

    public void add(int[] point) {
        counts[point[0]][point[1]]++;
    }

    public int count(int[] point) {
        int res = 0;
        for (int i = 0; i <= 1000; i++) {
            int x = point[0], y = point[1];
            int d = Math.abs(x - i);
            if (d == 0) continue;
            int j = y + d;
            if (j >= 0 && j <= 1000) {
                res += counts[i][j] * counts[i][y] * counts[x][j];
            }
            j = y - d;
            if (j >= 0 && j <= 1000) {
                res += counts[i][j] * counts[i][y] * counts[x][j];
            }
        }
        return res;
    }
}
