class DetectSquares {

    Map<Integer,Set<Integer>> map;
    Map<String,Integer> count;

    public DetectSquares() {
        map = new HashMap<>();
        count = new HashMap<>();
    }

    public void add(int[] point) {
        StringBuilder sb = new StringBuilder();
        sb.append(point[0]);
        sb.append("-"); sb.append(point[1]);
        String str = sb.toString();

        count.put(str, count.getOrDefault(str,0) + 1);

        map.putIfAbsent(point[0],new HashSet<>());
        map.get(point[0]).add(point[1]);
    }

    public int count(int[] point) {
        int x1 = point[0], y1 = point[1];

        if(!map.containsKey(x1)) return 0;

        Set<Integer> set = map.get(x1);
        int c = 0;

        for(int x2 : map.keySet()) {
            if(x1 == x2) continue;   

            Set<Integer> set2 = map.get(x2);

            if(!set2.contains(y1)) continue;

            for(int y2 : set2) {
                if(y1 == y2) continue;

                if(set.contains(y2)){

                    if(Math.abs(y2-y1) == Math.abs(x2-x1)){

                        StringBuilder sb = new StringBuilder();                

                        sb.append(x2);sb.append("-"); sb.append(y2);
                        int res = count.get(sb.toString());

                        sb = new StringBuilder();
                        sb.append(x2);sb.append("-"); sb.append(y1);
                        res *= count.get(sb.toString());

                        sb = new StringBuilder();
                        sb.append(x1);sb.append("-"); sb.append(y2);
                        res *= count.get(sb.toString());

                    c+=res;
                    }
                }

            }
        }
        return c;
    }
}

