class DetectSquares
    def initialize()
       @pts = Hash.new(0)
       @sums = Hash.new { |h, k| h[k] = [] }
       @difs = Hash.new { |h, k| h[k] = [] }
    end


=begin
    :type point: Integer[]
    :rtype: Void
=end
    def add(point)
       @pts[point] += 1
       x, y = point
       @sums[x + y] << point
       @difs[x - y] << point
    end


=begin
    :type point: Integer[]
    :rtype: Integer
=end
    def count(point)
       x, y = point
       (@sums[x + y] + @difs[x - y]).sum { |xx, yy| x == xx ? 0 : @pts[[x, yy]] * @pts[[xx, y]] }
    end
end
