    class DetectSquares {

        Map<Integer,Set<Integer>> xy=new HashMap();
        Map<Integer,Set<Integer>> yx=new HashMap();
        int points[][]=new int[1000+1][1000+1];

        public DetectSquares() {}

        public void add(int[] point) {
            points[point[0]][point[1]]++;
            xy.computeIfAbsent(point[0],p->new HashSet()).add(point[1]);
            yx.computeIfAbsent(point[1],p->new HashSet()).add(point[0]);
        }

        public int count(int[] point) {
            int count=0;
            int x1=point[0], y1=point[1];

            if( !xy.containsKey(x1)) { return 0; }

            for (int y2:xy.get(x1) ){
                int x2=x1;
                if( y1!=y2 ) {
                    int y3=y1, y4=y2;
                    for (int x3:yx.get(y2) ) {
                        int x4=x3;
                        if( x3!=x1 && points[x3][y3]>0 && points[x4][y4]>0 && Math.abs(x3-x2)==Math.abs(y2-y1) ) {
                            count+=points[x2][y2]*points[x3][y3]*points[x4][y4];
                        }
                    }
                }
            }
            return count;
        }
    }

    class DetectSquares2 {

        int points[][]=new int[1000+1][1000+1];

        public DetectSquares2() {}

        public void add(int[] point) {
            points[point[0]][point[1]]++;
        }

        public int count(int[] point) {
            int count=0;
            int x1=point[0], y1=point[1];
            // look for (x,y) on y line, excluding (x1,y1)
            for (int y2 = 0; y2 <=1000 ; y2++) {
                int x2=x1;
                if( points[x2][y2]>0 && y1!=y2 ) {
                    int y3=y1, y4=y2;
                    for (int x3 = 0; x3 <=1000 ; x3++) {
                        int x4=x3;
                        if( x3!=x1 && points[x3][y3]>0 && points[x4][y4]>0 && Math.abs(x3-x2)==Math.abs(y2-y1) ) {
                            count+=points[x2][y2]*points[x3][y3]*points[x4][y4];
                        }
                    }
                }
            }
            return count;
        }
    }

    void executeTest(String commands[], Object command_args[], Object expected[]) {
        log("command_args: " + command_args.length );

        DetectSquares squares=null;
        for (int i = 0; i < commands.length; i++) {
            String command=commands[i];
            Object arg=command_args[i];
            if( command.equalsIgnoreCase("DetectSquares") ){
                squares=new DetectSquares();
            }
            else if( command.equalsIgnoreCase("add") ){
                Object t[]=(Object[]) arg;
                int xy[]=new int[]{(Integer)t[0], (Integer)t[1]};
                squares.add(xy);
                //log(i + ", add: " + Arrays.toString(xy) );
            }
            else if( command.equalsIgnoreCase("count") ){

                Object t[]=(Object[]) arg;
                int xy[]=new int[]{(Integer)t[0], (Integer)t[1]};
                int value=squares.count(xy);
                int e=(Integer)expected[i];
                log(i + ", count: " + Arrays.toString(xy) + ", count: " + value );
                Assert.assertEquals(e,value);
            }
            else {
                throw new IllegalArgumentException("Unparsed: " + command );
            }
        }

    }

    public static void main(String args[]) {
        new DetectSquaresProblem();
    }
