type DetectSquares struct {
	points map[int]map[int]int
}

func Constructor() DetectSquares {
	return DetectSquares{
		points: make(map[int]map[int]int),
	}
}

func (this *DetectSquares) Add(point []int) {
	p, exist := this.points[point[0]]
	if !exist {
		p = make(map[int]int)
	}
	p[point[1]]++
	this.points[point[0]] = p
}

func (this *DetectSquares) Count(point []int) int {
	p, exist := this.points[point[0]]
	if !exist {
		return 0
	}
	yLen := len(p)
	xLen := len(this.points)
	ans := 0
	if yLen <= xLen {
		for y, times := range p {
			l := y - point[1]
			if l == 0 {
				continue
			}
			if m, exist := this.points[point[0]+l]; exist {
				ans += times * m[y] * m[point[1]]
			}
			if m, exist := this.points[point[0]-l]; exist {
				ans += times * m[y] * m[point[1]]
			}
		}
	} else {
		for x, m := range this.points {
			l := x - point[0]
			if l == 0 {
				continue
			}
			ans += m[point[1]] * p[point[1]+l] * m[point[1]+l]
			ans += m[point[1]] * p[point[1]-l] * m[point[1]-l]
		}
	}
	return ans
}
