class DetectSquares {
    private HashMap<Integer, List<Integer>> pointsByX = new HashMap<Integer, List<Integer>>();
    private HashMap<Integer, List<Integer>> pointsByY = new HashMap<Integer, List<Integer>>();

    private HashMap<Integer, Integer> pointCount = new HashMap<Integer, Integer>();

    public DetectSquares() {

    }

    public void add(int[] point) {
        // add count;
        int count = 0;
        int key = getKey(point);

        if(pointCount.containsKey(key)){
            count = pointCount.get(key);
        }
        else{
            // add new point to x map
            addPointToMap(pointsByX, point[0], point[1]);

            // add new point to y map
            addPointToMap(pointsByY, point[1], point[0]);
        }

        pointCount.put(key, ++count);
    }

    public int count(int[] point) {
        int result = 0;

        // get list of point matches x
        List<Integer> listY = pointsByX.get(point[0]);

        // get list of point matches y
