class DetectSquares(object):

    def __init__(self):
        self.mp = collections.defaultdict(lambda:collections.defaultdict(int))

    def add(self, point):
        """
        :type point: List[int]
        :rtype: None
        """
        self.mp[point[0]][point[1]] += 1


    def count(self, point):
        """
        :type point: List[int]
        :rtype: int
        """
        if point[0] not in self.mp:
            return 0
        ans = 0
        x = point[0]
        y = point[1]
        for ty in self.mp[x]:
            if ty == y:
                continue
            s = abs(y - ty)
            for tx in (x+s, x-s):
                if tx > 1000 or tx < 0:
                    continue
                ans += self.mp[x][ty] * self.mp[tx][y] * self.mp[tx][ty]
        return ans
