//second parameter saves the occurence

T.C=O(n);

class DetectSquares {










public:
     map<pair<int,int>, int>mp;

    DetectSquares() {

    }

    void add(vector<int> point) {
        mp[{point[0], point[1]}]++;
    }

    int count(vector<int> point) {

        int ans = 0;
        for(auto it : mp)
        {
            pair<int,int>p = it.first;
            //the first two condition checks coinciding points & third condition checks if its diagonal point or not
            if(p.first != point[0] and p.second != point[1] and (abs(point[0] - p.first) == abs(point[1] - p.second)))  
            { 
                //if p is diagonal point then other points are

                int first = mp[{p.first, p.second}]; //diagonal
                int second = mp[{p.first, point[1]}];
                int third = mp[{point[0], p.second}];

                int cur = first * second * third;
                ans += cur;
            }
        }

        return ans;
    }
};

