class DetectSquares {
    vector<vector<int>> x{1001};       
    vector<vector<int>> y{1001};  
    int cnt[2002][2002];

public:
    DetectSquares() { 
        memset(cnt,0,sizeof(cnt));
    }

    void add(vector<int> point) {
        x[point[0]].push_back(point[1]);
        y[point[1]].push_back(point[0]);

		//for storing the count
        cnt[point[0]][point[1]]++;
    }

    int count(vector<int> point) {
        int xc=point[0];
        int yc=point[1];
        int ans=0;
        for(int ys:x[xc]){
            if(ys==yc)continue;
            int len=abs(yc-ys);
            int x3,x4,y3,y4;

			//case-1
            x3=xc-len;
            x4=x3, y3=ys, y4=yc;

            int a=(x3<0 or y3<0)?0:cnt[x3][y3];
            int b=(x4<0 or y4<0)?0:cnt[x4][y4];
            ans+=a*b;

			//case-2
            x3=xc+len;
            x4=x3, y3=ys, y4=yc;

            a=(x3<0 or y3<0)?0:cnt[x3][y3];
            b=(x4<0 or y4<0)?0:cnt[x4][y4];
            ans+=a*b;
        }
        return ans;
    }
};

