        int x = point[0], y = point[1];
        for (int i = 0; i < 1001; i++) {
            if (i == x) continue;

            if (v[i][y] > 0) {
                int len = abs(i - x);
                if (y - len >= 0)
                    count += v[i][y] * v[i][y - len] * v[x][y - len];

                if (y + len <= 1000)
                    count += v[i][y] * v[i][y + len] * v[x][y + len];
            }
        }
        return count;
    }
};
