import kotlin.math.abs

class DetectSquares() {
    // map(X, map(Y, count))
    private val mapXtoY = mutableMapOf<Int, MutableMap<Int, Int>>()
    private val mapYtoX = mutableMapOf<Int, MutableMap<Int, Int>>()

    fun add(point: IntArray) {
        mapXtoY.getOrPut(point[0], { mutableMapOf() }).let {
            it[point[1]] = (it[point[1]] ?: 0) + 1
        }
        mapYtoX.getOrPut(point[1], { mutableMapOf() }).let {
            it[point[0]] = (it[point[0]] ?: 0) + 1
        }
    }

    fun count(point: IntArray): Int {
        return mapXtoY[point[0]]?.entries?.fold(0) { acc, entryY ->
            acc + if (entryY.key != point[1]) {
                val edge = abs(entryY.key - point[1])
                mapYtoX[entryY.key]!!.entries.fold(0) { acc2, entryX ->
                    acc2 + if (entryX.key != point[0] && abs(entryX.key - point[0]) == edge) {
                        (mapXtoY[entryX.key]!![point[1]] ?: 0) * entryY.value * entryX.value
                    } else { 0 }
                }
            } else { 0 }
        } ?: 0
    }
}
