class DetectSquares {
        int[][] plane;

    public DetectSquares() {
        //Initialize the plane to know the points added.
        plane = new int[1001][1001];
    }

    public void add(int[] point) {
        //Increment the value where the point is added.
        plane[point[0]][point[1]] += 1;
    }

    public int count(int[] point) {
        int count = 0;
        int x = point[0], y = point[1];
        for (int i = 0; i < 1001; i++) {
            //Ignore the current x value as a square is not possible
            if (i == x) continue;
            //Check if the value on the x - axis at same y - coordinate is greater than 0
            if (plane[i][y] > 0) {
                //Use the property of a square that it has equal lengths
                //If you found a point on the left, check for a square on both top and bottom 
                if (i < x) {
                    int diff = x - i;
                    //Check on the left bottom side of point and increment the count.
                    //Be careful to not to go beyond the plane i.e. y - diff >= 0
                    if (y - diff >= 0
                        && plane[i][y - diff] > 0
                        && plane[x][y - diff] > 0)
                        count += plane[i][y] * plane[i][y - diff] * plane[x][y - diff];
                    //Check on the left top side of point and increment the count.
                    //Be careful to not to go beyond the plane i.e. y + diff <= 1000
                    if (y + diff <= 1000
                        && plane[i][y + diff] > 0
                        && plane[x][y + diff] > 0)
                        count += plane[i][y] * plane[i][y + diff] * plane[x][y + diff];
                //If you found a point on the right, check for a square on both top and bottom
                } else {
                   int diff = i - x;
                    //Check on the right bottom side of point and increment the count.
                    //Be careful to not to go beyond the plane i.e. y - diff >= 0
                    if (y - diff >= 0
                        && plane[i][y - diff] > 0
                        && plane[x][y - diff] > 0)
                        count += plane[i][y] * plane[i][y - diff] * plane[x][y - diff];
                    //Check on the right top side of point and increment the count.
                    //Be careful to not to go beyond the plane i.e. y + diff <= 1000
                    if (y + diff <= 1000
                        && plane[i][y + diff] > 0
                        && plane[x][y + diff] > 0)
                        count += plane[i][y] * plane[i][y + diff] * plane[x][y + diff]; 
                }
            }
        }
        return count;
    }
}

