class DetectSquares {
    // Array stores the count of each point so far.
    int[][] pc;

    public DetectSquares() {
        // Since 0 <= x, y <= 1000.
        pc = new int[1001][1001];
    }

    public void add(int[] p) {
        // Add the point.
        pc[p[0]][p[1]]++;
    }

    public int count(int[] p) {
        // Stores the count of squares.
        int c = 0;

        // Traverse all 4 directions.
        // + , +
        int x=p[0]+1; int y = p[1]+1;
        while(x<=1000 && y <=1000) {
            if(pc[x][y] > 0 && pc[p[0]][y] > 0 && pc[x][p[1]] > 0) c += pc[x][y]* pc[p[0]][y]* pc[x][p[1]];
            x++; y++;
        }
        // - , +
        x=p[0]-1; y = p[1]-1;
        while(x>=0 && y >=0) {
            if(pc[x][y] > 0 && pc[p[0]][y] > 0 && pc[x][p[1]] > 0) c += pc[x][y]* pc[p[0]][y]* pc[x][p[1]];
            x--; y--;
        }
        // + , -
        x=p[0]+1; y = p[1]-1;
        while(x<=1000 && y >=0) {
            if(pc[x][y] > 0 && pc[p[0]][y] > 0 && pc[x][p[1]] > 0) c += pc[x][y]* pc[p[0]][y]* pc[x][p[1]];
            x++; y--;
        }
        // - , +
        x=p[0]-1; y = p[1]+1;
        while(x>=0 && y <=1000) {
            if(pc[x][y] > 0 && pc[p[0]][y] > 0 && pc[x][p[1]] > 0) c += pc[x][y]* pc[p[0]][y]* pc[x][p[1]];
            x--; y++;
        }

        return c;
    }
}

