    public class Point {
        int x;
        int y;

        public Point(int x, int y) {
            this.x = x;
            this.y = y;
        }

        public int getX() {
            return x;
        }

        public void setX(int x) {
            this.x = x;
        }

        public int getY() {
            return y;
        }

        public void setY(int y) {
            this.y = y;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            Point point = (Point) o;
            return x == point.x && y == point.y;
        }

        @Override
        public int hashCode() {
            return Objects.hash(x, y);
        }

        @Override
        public String toString() {
            return "Point{" +
                    "x=" + x +
                    ", y=" + y +
                    '}';
        }
    }

    class DetectSquares {
        Map<Point, Integer> points;

        public DetectSquares() {
            this.points = new HashMap<>();
        }

        public void add(int[] point) {
            Point nextPoint = new Point(point[0], point[1]);
            if (!points.containsKey(nextPoint)) {
                points.put(nextPoint, 0);
            }
            points.put(nextPoint, points.get(nextPoint) + 1);
        }

        public int count(int[] point) {
            int res = 0;
            Point queryPoint = new Point(point[0], point[1]);
            for (Point dot : points.keySet()) {
                if (Math.abs(queryPoint.getX() - dot.getX()) == Math.abs(queryPoint.getY() - dot.getY()) && !queryPoint.equals(dot)) {
                    Point dot1 = new Point(queryPoint.getX(), dot.getY());
                    Point dot2 = new Point(dot.getX(), queryPoint.getY());
                    if (points.containsKey(dot1) && points.containsKey(dot2)) {
                        res += points.get(dot1) * points.get(dot2) * points.get(dot);
                    }
                }
            }
            return res;
        }
    }

