struct PointHash {
    size_t operator() (const vector<int> a) const {
        return a[0] ^ a[1];
    }
};

class DetectSquares {
public:
    unordered_map<vector<int>, int, PointHash> pCount;
    unordered_map<int, unordered_set<vector<int>, PointHash>> yPoints;

    DetectSquares() {}

    void add(vector<int> point) {
        pCount[point]++;
        yPoints[point[1]].insert(point);
    }

    int count(vector<int> point) {
        int ans = 0;
        for (vector<int> t1 : yPoints[point[1]]) { // Look for all y-equal point (t1), where dx != 0
            int dim = abs(point[0] - t1[0]);
            if (dim == 0) continue;
            if (pCount.count({ t1[0], t1[1] + dim })) { // Look for point above t1 (b1)
                vector<int> b1 = { t1[0], t1[1] + dim };
                if (pCount.count({point[0], point[1] + dim})) { // Look for point above p (b2)
                    vector<int> b2 = { point[0], point[1] + dim };
                    ans += pCount[t1] * pCount[b1] * pCount[b2];
                }
            }
            if (pCount.count({ t1[0], t1[1] - dim })) { // Look below
                vector<int> b1 = { t1[0], t1[1] - dim };
                if (pCount.count({ point[0], point[1] - dim })) {
                    vector<int> b2 = { point[0], point[1] - dim };
                    ans += pCount[t1] * pCount[b1] * pCount[b2];
                }
            }
        }
        return ans;
    }
};

