class DetectSquares {
public:
    DetectSquares() {
    }

    void add(vector<int> point) {
        mp[point[0]][point[1]]++;
    }

    // 1. Loop Through all the points
    // 2. If (p,q) formas diagonal with any of the points, then we found a square
    int count(vector<int> point) {
        int p=point[0];
        int q=point[1];

        int ans=0;
        for(auto v1: mp){
            int x=v1.first;
            for(auto v2:v1.second){
                int y=v2.first;

                // 3. Skip if the point (p,q) is in the same x or y axis as (x,y)
                if(p==x || q==y) continue;

                // 4. Counting the frequency of other 3 points in the square besides (p,q)
                //    Check if the distance between (p,q) and (x,y) is equal in both dimensions (forms a diagonal)
                if(abs(x-p)==abs(y-q)) ans+= mp[x][y] * mp[x][q] * mp[p][y];
            }
        }
        return ans;
    }

private:
    // {x, {y, count}} 
    unordered_map<int, unordered_map<int, int>> mp;
};

