        map.putIfAbsent(x, new HashMap<>());
        Map<Integer, Integer> mapY = map.get(x);
        mapY.put(y, mapY.getOrDefault(y, 0) + 1);
    }

    public int count(int[] point) {
        int x = point[0];
        int y = point[1];
        if (!map.containsKey(x))
            return 0;
        Map<Integer, Integer> mapY = map.get(x);
        int res = 0;
        for (int py: mapY.keySet()) {
            if (py == y)
                continue;
            int len = Math.abs(y-py);
            if (map.containsKey(x+len) && map.get(x+len).containsKey(y) && map.get(x+len).containsKey(py))
